INSERT INTO `wp_options` VALUES (107, 0, 'dashboard_widget_options', 'a:3:{s:24:"dashboard_incoming_links";a:5:{s:4:"home";s:23:"http://scottbulloch.com";s:4:"link";s:105:"http://blogsearch.google.com/blogsearch?hl=en&scoring=d&partner=wordpress&q=link:http://scottbulloch.com/";s:3:"url";s:138:"http://blogsearch.google.com/blogsearch_feeds?hl=en&scoring=d&ie=utf-8&num=10&output=rss&partner=wordpress&q=link:http://scottbulloch.com/";s:5:"items";i:10;s:9:"show_date";b:0;}s:17:"dashboard_primary";a:7:{s:4:"link";s:33:"http://wordpress.org/development/";s:3:"url";s:38:"http://wordpress.org/development/feed/";s:5:"title";s:26:"WordPress Development Blog";s:5:"items";i:2;s:12:"show_summary";i:1;s:11:"show_author";i:0;s:9:"show_date";i:1;}s:19:"dashboard_secondary";a:4:{s:4:"link";s:28:"http://planet.wordpress.org/";s:3:"url";s:33:"http://planet.wordpress.org/feed/";s:5:"title";s:20:"Other WordPress News";s:5:"items";i:5;}}', 'yes');
