INSERT INTO `wp_options` VALUES (114, 0, 'rss_a5420c83891a9c88ad2a4f04584a5efc', 'O:9:"MagpieRSS":17:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:15:{i:0;a:7:{s:5:"title";s:27:"donncha on "WP Super Cache"";s:4:"link";s:61:"http://wordpress.org/extend/plugins/wp-super-cache/#post-2572";s:7:"pubdate";s:31:"Mon, 05 Nov 2007 11:40:04 +0000";s:2:"dc";a:1:{s:7:"creator";s:7:"donncha";}s:4:"guid";s:41:"2572@http://wordpress.org/extend/plugins/";s:11:"description";s:73:"A very fast caching engine for WordPress that produces static html files.";s:7:"summary";s:73:"A very fast caching engine for WordPress that produces static html files.";}i:1;a:7:{s:5:"title";s:17:"matt on "Akismet"";s:4:"link";s:52:"http://wordpress.org/extend/plugins/akismet/#post-15";s:7:"pubdate";s:31:"Fri, 09 Mar 2007 22:11:30 +0000";s:2:"dc";a:1:{s:7:"creator";s:4:"matt";}s:4:"guid";s:39:"15@http://wordpress.org/extend/plugins/";s:11:"description";s:98:"Akismet checks your comments against the Akismet web service to see if they look like spam or not.";s:7:"summary";s:98:"Akismet checks your comments against the Akismet web service to see if they look like spam or not.";}i:2;a:7:{s:5:"title";s:33:"uberdose on "All in One SEO Pack"";s:4:"link";s:65:"http://wordpress.org/extend/plugins/all-in-one-seo-pack/#post-753";s:7:"pubdate";s:31:"Fri, 30 Mar 2007 20:08:18 +0000";s:2:"dc";a:1:{s:7:"creator";s:8:"uberdose";}s:4:"guid";s:40:"753@http://wordpress.org/extend/plugins/";s:11:"description";s:92:"Automatically optimizes your Wordpress blog for Search Engines (Search Engine Optimization).";s:7:"summary";s:92:"Automatically optimizes your Wordpress blog for Search Engines (Search Engine Optimization).";}i:3;a:7:{s:5:"title";s:32:"takayukister on "Contact Form 7"";s:4:"link";s:61:"http://wordpress.org/extend/plugins/contact-form-7/#post-2141";s:7:"pubdate";s:31:"Thu, 02 Aug 2007 12:45:03 +0000";s:2:"dc";a:1:{s:7:"creator";s:12:"takayukister";}s:4:"guid";s:41:"2141@http://wordpress.org/extend/plugin